$.fn.sorter=function(t){var r=$(this);r.find("th").click(function(){var t=$(this).attr("order")||"ASC",e=$(this).index(),s=[];r.find("tr:gt(0)").each(function(t,r){s.push($(r).children("td").eq(e))}),"DESC"==t&&s.reverse(),s.sort(function(r,e){return r.html()>e.html()?("ASC"==t?$(r).closest("tr").insertAfter($(e).closest("tr")):$(r).closest("tr").insertBefore($(e).closest("tr")),1):r.html()<e.html()?("ASC"==t?$(e).closest("tr").insertBefore($(r).closest("tr")):$(r).closest("tr").insertAfter($(e).closest("tr")),-1):0}),$(this).attr("order","ASC"==t?"DESC":"ASC")})};
